import React, { memo, useEffect } from 'react';
import styled from 'styled-components';
import { useSelector } from  'react-redux';


const BasketContainer = styled.div`
    width: 65%;
    height: 200px;
    background-color: white;
    border-radius: 5px;
    margin:20px 10px;
    .itemBox{
        display: flex;
        flex-direction: row;
        justify-content: space-evenly;
        flex-wrap: wrap;
  
        .itemList{
            text-align: center;
            margin: auto;
            padding: 15px 0;
            .imageBox{
                border: 1px solid #eee;
                width: 100px;
                height: 100px;
                box-sizing: border-box;
                margin: 10px; 
            }
            h3{
                font-weight: 700;
                padding: 5px 0 ;
                font-size: 14px;
            }
            P{
                padding-top: 5px;
                font-size: 12px;
            }
        }
    }
`;
const ItemBasket = memo(() => {
    useEffect(()=>console.clear(),[]);


    const {basket,loading, error} = useSelector((state)=>state.basket);

    return (
        <BasketContainer>
                {loading ?(
                    <p> Loading...</p>
                ): error?(
                <div>
                <h1>이와 같은 에러가 발생했습니다. <br/> : {error.code}</h1>
                </div>
            ):(
                <div className='itemBox'>
                 {basket.basketItems?.map(basketItem=>(
                    <div key={basketItem.id} className="itemList">
                        <div className='basketItem'>
                            <img className='imageBox' src={basketItem.image} alt={basketItem.name} />
                            <div>
                                <h3>{basketItem.name}</h3>
                                <button>주문삭제</button>
                            </div>
                        </div>
                        <div className='basketItem-price'>{basketItem.price}</div>
                        <div className='basketItem-quantity'>
                            <button>-</button>
                            <div className='count'>{basketItem.basketQuantity}</div>
                            <button>-</button>
                        </div>
                        <div className='total-price'>
                            ${basketItem.price * basketItem.basketQuantity}
                        </div>
                    </div>
                ))}
                </div>
            )}
        </BasketContainer>
    );
});

export default ItemBasket;
