import React, { memo } from 'react';
import styled from 'styled-components';
import logo from '../assets/img/logo/logo.png';

const Header = styled.div`
    width: 960px;
    height: 120px;
    margin: 0 auto;
    background-color: #fec24a;
    .first-line{
        width: 90%;
        height: 50px;
        margin: auto;

        .icon{
            display: flex;
            flex-direction: row;
            justify-content: space-between;
            align-items: center;
            padding: 10px 0;
            .logo{
                width: 40px;
                height: 40px;
                margin-top: 10px;
            }
            .title{
                color: white;
                font-size: 20px;
                font-weight: 700;
            }
        }
    }
    .main-menu{
        width: 90%;
        height: 50px;
        margin: auto;
        padding: 10px 0;

        .category{
            display: block;
           
            ul{
                display: flex;
                flex-direction: row;
                justify-content: space-between;
                line-height: 50px;
                
                li{
                    font-size: 16px;
                    font-weight: 600;
                    color: white;
                    padding: 5px 20px;
                    cursor: pointer;
                    
                    &:hover{
                        background-color: white;
                        color: black;
                        border-radius: 5px 5px 0 0;
                    }
                }
            }
        }
    }
`;

const header = memo(() => {
    return (
        <Header>
            <div className='first-line'>
                <div className='icon'>
                    <img className='logo' src={logo} alt="mainLogo" />
                    <p className='title'>Easy KIOSK</p>
                    <div className='empty'></div>
                </div>
            </div>
            <div className='main-menu'>
                <div className='category'>
                    <ul>
                        <li>케이크</li>
                        <li>마카롱</li>
                        <li>크로플</li>
                        <li>샌드위치</li>
                        <li>쿠키</li>
                        <li>빵</li>
                        <li>기타</li>
                    </ul>
                </div>
            </div>
        </Header>
    );
});

export default header;
