import React, { memo, useCallback } from 'react';
import styled from "styled-components";
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';

const PayBtn = styled.div`
    width: 90%;
    margin: auto;
    text-align: center;
    height: 33.3%;
    display: flex;
    align-items: center;
    .item-payBtn{
        align-items: center;
        background-color: #d9d8d8;
        color: black;
        font-size: 14px;
        font-weight: 700;
        width: 100%;
        height: 90%;
        line-height: 450%;
        border-radius: 5px;
        cursor: pointer;
        &:hover{
            background-color: #fec24a;
            color: white;
        }
    }
`;
const PayButton = memo(() => {
    const MySwal = withReactContent(Swal);

    // Promise 방식을 사용한 다이얼로그
    const onButton1Click = useCallback(() => {
        MySwal.fire({
            // 불필요한 항목은 삭제 가능함
            title: 'Promise',
            text: 'SweetAlert을 활용한 메시지 박스 테스트 입니다.',
            icon: 'info',
            footer: '<a href="https://sweetalert2.github.io/">SweetAlert2에 대해 궁금한가요?</a>'
        }).then((result) => {
            console.debug(result);
            if (result.isConfirmed) {
                MySwal.fire('확인 버튼을 눌렀습니다.');
            }
        });
    }, [MySwal]);
    return (
        <PayBtn>
            <div className='item-payBtn'>
            결제하기
            </div>
        </PayBtn>
    );
});

export default PayButton;
