import {createSlice, createAsyncThunk} from "@reduxjs/toolkit"
// 액션함수를 위한 hooks 사용
import {pending, fulfilled, rejected} from '../Action';
import axios from 'axios';

const API_URL = {
  ca:"http://localhost:3001/product/1/",
  m:"http://localhost:3001/product/2/",
  cr:"http://localhost:3001/product/3/",
  s:"http://localhost:3001/product/4/",
  ck:"http://localhost:3001/product/5/",
  b:"http://localhost:3001/product/6/",
  e:"http://localhost:3001/product/7/",
};

/* 다중행 데이터 조회를 위한 비동기 함수 */
export const getList = createAsyncThunk('LargeBreadSlice/getList', async(payload,{rejectValue})=>{
  let result = null;
  
  const params = {};
  if (payload?.product_name){
    params.product_name = payload.name
  }

  try {
    result = await axios.get(API_URL[payload.api ? payload.api : 1]);
  }catch(err){
    result = rejectValue(err.response);
  }
  return result;
});

/* 단일행 데이터 조회를 위한 비동기 함수 */
export const getItem  = createAsyncThunk('LargeBreadSlice/ getItem', async(payload, {rejectWithValue})=>{
  let result = null;

  try{
    result= await axios.get(`${API_URL}${payload?.id}/`);
  }catch(err){
    result = rejectWithValue(err.response);
  }
  return result;
});

/* 데이터 저장을 위한 비동기 함수 */
export const postItem = createAsyncThunk('LargeBreadSlice/postItem', async(payload, { rejectWithValue})=>{
  let result = null;
  
  try{
    result = await axios.post(API_URL,{
      product_name: payload.product_name,
      img_url: payload.img_url,
      price: payload.price
    })
  }catch(err){
    result = rejectWithValue(err.response);
  }
  return result;
});

/* 데이터 수정을 위한 비동기 함수 */
export const putItem = createAsyncThunk('LargeBreadSlice/putItem', async(payload, { rejectWithValue})=>{
  let result = null;
  
  try{
    result = await axios.put(`${API_URL}${payload?.id}/`,{
      product_name: payload.product_name,
      img_url: payload.img_url,
      price: payload.price
    })
  }catch(err){
    result = rejectWithValue(err.response);
  }
  return result;
});
/* 데이터 삭제를 위한 비동기 함수 */
export const deleteItem = createAsyncThunk('LargeBreadSlice/deleteItem', async(payload, { rejectWithValue})=>{
  let result = null;
  
  try{
    result = await axios.delete(`${API_URL}${payload?.id}/`);
  }catch(err){
    result = rejectWithValue(err.response);
  }
  return result;
});

const LargeBreadSlice = createSlice({
  name: 'LargeBreadSlice',
  initialState: {
    data: null,       
    loading: false, 
    error:null        
  },
  reducers: {},
  extraReducers: { 
  /* 다중행 데이터 조회를 위한 액션 함수 */
  [getList.pending]: pending,
  [getList.fulfilled]: fulfilled,
  [getList.rejected]: rejected,

  /* 단일행 데이터 조회를 위한 액션 함수 */
  [getItem.pending]: pending,
  [getItem.fulfilled]: fulfilled,
  [getItem.rejected]: rejected,

  /* 데이터 저장을 위한 액션 함수 */
  [postItem.pending]: pending,
  [postItem.fulfilled]: (state,{meta,payload})=>{
    let data = null;

    if(Array.isArray(state.data)){
      data=[...state.data];
      data.push(payload.data);
    }else{
      data=payload.data;
    }
    return{
      data: data,
      loading: false,
      error: null
    }
  },
  [postItem.rejected]: rejected,

  /* 데이터 수정을 위한 액션 함수 */
  [putItem.pending]: pending,
  [putItem.fulfilled]: (state,{meta, payload})=>{
    let data = null;

    if(Array.isArray(state.data)){
      data= [...state.data];

      const index = data.findIndex(element => element.id === parseInt(meta.arg.id));

      if(index !== undefined){
        data.splice(index, 1, payload.data);
      }
    }else{
      data = payload.data;
    }
    return{
      data: data,
      loading: false,
      error: null
    }
  },
  [putItem.rejected]: rejected,

  /* 데이터 삭제를 위한 액션 함수 */
  [deleteItem.pending]: pending,
  [deleteItem.fulfilled]: (state,{meta,payload})=>{
    let data = null;

    if(Array.isArray(state.data)){
      data = [...state.data];

      const index = data.findIndex(element => element.id === parseInt(meta.arg.id));
      console.log('index=' + index);

      if(index !== undefined){
        data.splice(index, 1);
      }
    }
    return{
      data: data,
      loading:false,
      error: null
    }
  },
  [deleteItem.rejected]: rejected,

  },
});

export default LargeBreadSlice.reducer;
