import React, { memo, useEffect } from 'react';
import styled from 'styled-components';
import {useSelector,  useDispatch } from  'react-redux';
import {cleanBasket} from '../slices/BasketSlice'

const DelBtn = styled.div`
width:30%;
.delBtn{
    width: 100%;
    font-size: 14px;
    font-weight: 700;
    color: white;
    background-color: #4c4747;
    text-align: center;
    padding: 25px 17px;
    border-radius: 5px;
    &:hover{
        background-color: black;
    }
}
`;
const DelButton = memo(() => {
    useEffect(()=>console.clear(),[]);
    const dispatch = useDispatch();

    const handleCleanFromBasket = (basketItem)=>{
        dispatch(cleanBasket(basketItem));
    }
    const basket = useSelector((state)=>state.basket);
    return (
        <DelBtn>
        {basket.basketItems?.map(basketItem=>(
            <div className='delBtn' onClick={()=>handleCleanFromBasket(basketItem)}>
                전체삭제
            </div>
               ))}
        </DelBtn>
    );
});

export default DelButton;
