import React, { memo } from 'react';
import styled from 'styled-components';
import DelButton from './DelButton';
import ItemButton from './ItemButton';
import PayButton from './PayButton';


const ButtonContainer = styled.div`
    width: 30%;
    height: 260px;
    background-color: #c0c0c0;
    border-radius: 5px;
    margin:20px 10px;
    
    .system-info{
        width: 80%;
        height: 50px;
        margin: auto;
        display: flex;
        flex-direction: row;
        justify-content: space-around;
        align-items: center;
        margin-top: 10px;
        .time-info{
            text-align: center;
            font-weight: 700;
            h3{
                font-size: 14px;
                padding: 5px 0;
            }
            p{
                font-size: 14px;
                b{
                    color: #ae2a2f;
                    font-size: 20px;
                    font-weight: 900;
                }
                
            }
        }
    }
`;

const ItemBox = memo(() => {
    return (
        <ButtonContainer>
            <div className='system-info'>
            <div className='time-info'>
                <h3>남은시간</h3>
                <p><b>100</b>초</p>
            </div>
            <DelButton/>
            </div>
            <ItemButton/>
            <PayButton/>
        </ButtonContainer>
    );
});

export default ItemBox;
