import React, { memo } from 'react';
import styled from 'styled-components';

const ModalContainer= styled.div`
.modalBox{
    position:fixed;
    top:50%;
    left:50%;
    z-index: 100;
    width: 250px;
    height:150px;
    transform: translate(-50%,-50%);
    background: white;
    border:1px solid #7e7e7e;
    
    .modalInner{
        padding : 10px;
        
        button{
            width:100%;
            height:30px;
            margin-top:10px;
            border: none;
            background-color: #98D6F6;
            color: #fff;
            border-radius: 3px;
        }
    }
    .modalItem{
        padding:27px 5%;
        height: 33px;
        p{
            text-align: center;
            line-height:1.3;
        }
    }
}
`;
const Modal = memo(({modal, setModal}) => {
    const onPayModal = React.useCallback(() => {
        setModal(false);
    },[setModal]);

    return (
        <ModalContainer>
            {modal && 
                <div className="modalBox">
                <div className="modalInner">
                    <div className="modalItem">
                        <p>결제가 완료되었습니다.</p>
                    </div>
                    <button onClick={onPayModal}>확인</button>
                </div>
            </div>
            }
        </ModalContainer>
    );
});
 
export default Modal;
