import React, { memo, useEffect } from 'react';
import styled from 'styled-components';
import { useSelector, useDispatch } from 'react-redux';
import {getList} from '../slices/LargeBreadSlice';
import {addToBasket} from '../slices/BasketSlice';

const SingleItemContainer = styled.div`
height: 100%;
width: 100%;
    .itemBox{
        display: flex;
        flex-direction: row;
        justify-content: space-evenly;
        flex-wrap: wrap;
  
        .itemList{
            text-align: center;
            margin: auto;
            padding: 10px 0;
            .imageBox{
                width: 150px;
                height: 150px;
                box-sizing: border-box;
                margin: 10px; 
            }
            h3{
                font-weight: 700;
                padding:5px 0 ;
            }
            P{
                padding-bottom:10px;
            }
            .choice{
                width: 150px;
                height: 40px;
                border-radius: 5px;
                margin: auto;
                border: 2px solid #ae2a2f;
                font-weight: 700;
                color: #222;
                &:hover{
                    background-color: #ae2a2f;
                    color: white;
                    
                }
            }
        }
    }
`;
const SingleItem = memo(() => {
 
    useEffect(()=>console.clear(),[]);

    const {data,loading, error} = useSelector((state)=>state.LargeBreadSlice);
    const dispatch = useDispatch();

    const handleAddToBasket =(item) =>{
        dispatch(addToBasket(item));    
    }

    useEffect(()=>{
        dispatch(getList());
    }, [dispatch]);
  
    return (
        <SingleItemContainer>
            {loading ?(
                    <p> Loading...</p>
                ): error?(
                <div>
                    <h1>이와 같은 에러가 발생했습니다. <br/> : {error.code}</h1>
                </div>
            ):(
                <div className='itemBox'>
                {data && data.map((item,i)=>(
                    <div key={i} className="itemList">
                        <img className='imageBox' src={item.image} alt={item.name} />
                        <h3>{item.name}</h3>
                        <p>{item.price}원</p>
                        <button onClick={()=> handleAddToBasket(item)} className='choice' data-id={item.id}>
                            선택하기
                        </button>
                    </div>
                ))}
                </div>
            )}
        </SingleItemContainer>
    );
});

export default SingleItem;
